lse;				}				return true;			}						function remove(str, sub){				i = str.indexOf(sub);				r = "";				if (i == -1) return str;{					r += str.substring(0,i) + remove(str.substring(i + sub.length), sub);				}				return r;			}						function validarCNPJ(cnpj){				cnpj = cnpj.replace(/[^\d]+/g,'');				if(cnpj == '') return false;				if (cnpj.length != 14){					document.cadastro.doctipo.focus();					alert("CNPJ inválido! Verifique o número digitado.");					return false;				}				if (cnpj == "00000000000000" || cnpj == "11111111111111" || 						cnpj == "22222222222222" || cnpj == "33333333333333" || 						cnpj == "44444444444444" || cnpj == "55555555555555" || 						cnpj == "66666666666666" || cnpj == "77777777777777" || 						cnpj == "88888888888888" || cnpj == "99999999999999"){					document.cadastro.doctipo.focus();					alert("CNPJ inválido! Verifique o número digitado.");					return false;				}				tamanho = cnpj.length - 2				numeros = cnpj.substring(0,tamanho);				digitos = cnpj.substring(tamanho);				soma = 0;				pos = tamanho - 7;				for (i = tamanho; i >= 1; i--) {					soma += numeros.charAt(tamanho - i) * pos--;					if (pos < 2){						pos = 9;					}				}				resultado = soma % 11 < 2 ? 0 : 11 - soma % 11;				if (resultado != digitos.charAt(0)){					document.cadastro.doctipo.focus();					alert("CNPJ inválido! Verifique o número digitado.");					return false;				}    				tamanho = tamanho + 1;				numeros = cnpj.substring(0,tamanho);				soma = 0;				pos = tamanho - 7;				for (i = tamanho; i >= 1; i--){					soma += numeros.charAt(tamanho - i) * pos--;					if (pos < 2){					pos = 9;}				}				resultado = soma % 11 < 2 ? 0 : 11 - soma % 11;				if (resultado != digitos.charAt(1)){					document.cadastro.doctipo.focus();					alert("CNPJ inválido! Verifique o número digitado.");					return false;				}				return true;			} 						$(document).ready				(function(){					$('#cpf').change					(function(){						$('#al