ar", ".pdf", ".png");				var tamanhoArquivo = parseInt(document.getElementById("arquivo").files[0].size);				if (!arquivo){ 					valido = false;					//Se não tenho arquivo, é porque não se selecionou um arquivo no formulário. 					alert('NENHUM ARQUIVO SELECIONADO!');				}else if (tamanhoArquivo > 26214400){//25Mb.					valido = false;					alert("TAMANHO DO ARQUIVO NÃO PERMITIDO!\n\nTamanho máximo: 25Mb");								}else{ //Recupera a extensão deste nome de arquivo... 					extensao = (arquivo.substring(arquivo.lastIndexOf("."))).toLowerCase(); 					permitida = false; 					for (var i = 0; i < extensoes_permitidas.length; i++){ 						if (extensoes_permitidas[i] == extensao){ 							permitida = true; 							break; 						} 					} 					if (!permitida){ 						valido = false;						alert('EXTENSÃO DO ARQUIVO NÃO PERMITIDA!\n\nExtensões permitidas: ' + extensoes_permitidas.join()); 					}else{ 						valido = true;					} 				} 				return valido; 			}						setTimeout(function(){$('#alert').fadeOut('slow');}, 5000);			setTimeout(function(){$('#alerts').fadeOut('slow');}, 5000);						function desabilitar(){ 				return false; 			} 						document.oncontextmenu=desabilitar;						function recolher_painel(el){				var display = document.getElementById(el).style.display;								if(display == "none")					document.getElementById(el).style.display = 'block';				else					document.getElementById(el).style.display = 'none';			}					</script>				</head>		<?php			if(!isset($_SESSION['id_session'])){									echo "			<fieldset>				<div align='center' class='warning'><img class='imgnatural' src='../imagens/denied.png'/>				<br><b>HEAD: Sessão inexistente. Faça login...</b>				</div>			</fieldset>";										exit;				}					if($SASTATUS != 1){			echo "			<fieldset><legend class='subpageName'>&nbsp;&nbsp;Acesso negado&nbsp;&nbsp;</legend>				<div align='center' class='warning'><img class='imgnatural' src='../imagens/denied.png'/>				<b